mapper.update:
UPDATE aixin_order_record
status = #{orderRecord.status}, total_fuzhuang = #{orderRecord.totalFuzhuang}, total_riyong = #{orderRecord.totalRiyong},
WHERE id = #{orderRecord.id}

mapper.select:
SELECT re.id, re.create_time, re.campus, re.status, re.total_fuzhuang, re.total_riyong, student.id as sid, student.balance_riyong, student.balance_fuzhuang, student.contact, student.campus as scampus, info.name, info.grade, info.stuNum, department.departmentName, imbursetype.imburseTypeName, special.specialName, de.id as did, de.goods_num, g.id as gid, g.barcode, g.goods_name, g.specs, g.money_type, g.goods_type, t.classification, g.price, g.limit_buy_num, g.limit_buy_type, g.images FROM aixin_order_record re LEFT JOIN aixin_student student ON re.buyer_id = student.id LEFT JOIN aixin_order_detail de ON de.order_id = re.id LEFT JOIN aixin_goods g ON de.goods_id = g.id, info_student info, param_department department, param_imburseType imbursetype, param_special special, aixin_goods_type t
student.info_student_id=info.id and info.departmentId=department.id and info.specialNum=special.id and info.imburseType=imbursetype.id and g.goods_type = t.id # re.id = #{orderRecordId}() and re.id = #{orderRecordId} and re.buyer_id = #{orderRecord.student.id} and re.campus = #{orderRecord.campus} and re.status = #{orderRecord.status}
and info.stuNum = #{orderRecord.student.stuNum}
and info.name LIKE concat('%',#{orderRecord.student.name}, '%')
ORDER BY re.create_time DESC LIMIT #{pageIndex},#{pageSize}

SELECT COUNT(1)
FROM aixin_order_record re,
  info_student info,
aixin_student student

    student.info_student_id=info.id
    and re.buyer_id = student.id
    and re.buyer_id = #{orderRecord.student.id}
    and re.campus = #{orderRecord.campus}
    and re.status = #{orderRecord.status}

        and info.name LIKE concat('%',#{orderRecord.student.name}, '%')

select re.id, buyer_id, create_time, status, total_riyong, total_fuzhuang, re.campus, info.id as sid FROM aixin_order_record re, info_student info, aixin_student student
student.info_student_id = info.id AND re.buyer_id = student.id and re.buyer_id = #{orderRecord.student.id} and re.campus = #{orderRecord.campus} and re.status = #{orderRecord.status}
and info.stuNum = #{orderRecord.student.stuNum}
and info.name LIKE concat('%',#{orderRecord.student.name}, '%')
ORDER BY re.create_time DESC LIMIT #{pageIndex},#{pageSize}
