mapper.update:
UPDATE aixin_student
password = #{student.password}, contact = #{student.contact}, campus = #{student.campus}, balance_riyong = #{student.balanceRiyong}, balance_fuzhuang = #{student.balanceFuzhuang},
WHERE id = #{student.id}

UPDATE aixin_student, info_student
aixin_student.balance_riyong = #{student.balanceRiyong}, aixin_student.balance_fuzhuang = #{student.balanceFuzhuang},
WHERE aixin_student.info_student_id = info_student.id and info_student.imburseType = #{imburseType}

mapper.select:
SELECT  count(1)
FROM aixin_student student,
info_student info,
param_department department,
param_imburseType imbursetype,
param_special special
 student.info_student_id=info.id
    and info.departmentId=department.id
    and info.specialNum=special.id
    and info.imburseType=imbursetype.id
    and info.departmentId = #{studentType.departmentId}
    and info.specialNum = #{studentType.specialId}
    and info.stuNum = #{studentType.stuNum}
    and info.name = #{studentType.name}
    and info.state = #{studentType.state}
    and info.grade = #{studentType.grade}
    and info.imburseType = #{studentType.imburseType}
