mapper.select:
SELECT warehouse.id, warehouse.num, warehouse.campus, goods.id as goods_id, goods.goods_name, goods.specs, goods.price, goods.money_type, goods.limit_buy_type, goods.limit_buy_num, goods.images, goods.goods_type, t.classification, goods.barcode FROM aixin_warehouse warehouse, aixin_goods goods, aixin_goods_type t warehouse.goods_id = goods.id AND goods.goods_type = t.id AND warehouse.campus = #{warehouse.campus}
AND goods.goods_type = #{warehouse.goods.goodsType}
AND goods.money_type = #{warehouse.goods.moneyType}
AND goods.goods_name LIKE concat('%',#{warehouse.goods.goodsName}, '%')
ORDER BY goods.price asc
ORDER BY goods.price DESC
LIMIT #{pageIndex},#{pageSize}

SELECT COUNT(1)
FROM aixin_warehouse warehouse,
aixin_goods goods,
aixin_goods_type t
 warehouse.goods_id = goods.id
    AND goods.goods_type = t.id
    AND warehouse.campus = #{warehouse.campus}

        AND goods.goods_type = #{warehouse.goods.goodsType}


        AND goods.money_type = #{warehouse.goods.moneyType}


        AND goods.goods_name LIKE concat('%',#{warehouse.goods.goodsName}, '%')
